import React from "react";

interface RowInterface {
	children: React.ReactNode;
	width?: number;
}

const Row = (props: RowInterface) => {
	return (
		<tr
			style={{
				width: props.width ? `${props.width}%` : "100%",
			}}
		>
			{props.children}
		</tr>
	);
};

export default Row;
